class CityTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object CityTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('City');
    }

    public function getCityNames($culture = null)
    {
        
        if (!$culture) {
            $culture = sfContext::getInstance()->getUser()->getCulture();
        }

        $query = Doctrine_Core::getTable('City')
            ->createQuery('q')
            ->innerJoin('.Translation ct')
            ->orderBy('name ASC');
        return $query->execute();
    }

    public static function defaultCitiesQuery($country_id = null)
    {
        $query = self::getInstance()->createQuery('c')
            ->where('is_default > 0');
        
        if (!is_null($country_id)) {
            $query = $query
                ->innerJoin('c.County co')
                ->addWhere('co.country_id = ?', $country_id);
        }

        return $query;
    }

    public static function getByAddress($address, $country_id = null)
    {
        $tmp = explode(',', $address);
        $search_city = trim(array_shift($tmp));
        $search_country = trim(array_pop($tmp));

        $query = Doctrine::getTable('City')
                ->createQuery('c')
                ->innerJoin('c.County co')
                ->innerJoin('c.Translation ct')
                ->where('ct.name = ?', $search_city);
        if ($country_id) {
             $query->addWhere('co.country_id = ?', $country_id);
        } else {
            $query->innerJoin('co.Country cty')
                    ->addWhere('cty.name = ? OR cty.name_en = ?', array($search_country, $search_country));
        }

        return $query->fetchOne();
    }

    public static function getAutocompleteJSON($term, $exclude = array(), $country_id = null)
    {
        $term = '%' . $term . '%';
        $query = self::getInstance()->createQuery('c')
            ->select('c.id, ct.name')
            ->innerJoin('c.Translation ct')
            ->whereNotIn('c.id', $exclude)
            ->addWhere('ct.name LIKE ?', $term);

        if (!is_null($country_id)) {
            $query->innerJoin('c.County co')->addWhere('co.country_id = ?', $country_id);
        }

        $cities = $query->execute();
        $data = array();
        foreach ($cities as $c) {
            $data[] = array(
                'label' => $c->getName(),
                'value' => $c->getName(),
                'id' => $c->getId()
            );
        }

        return $data;
    }

    public static function findOrCreate($city, $county, $country, $lat = null, $lng = null)
    {
        // find the city in that country
        $cityTerm = "{$city}";
        $countyTerm = "{$county}";
        $countryTerm = "{$country}";

        if (!$country) {
            return null;
        }
        $query = self::getInstance()->createQuery('c')
            ->innerJoin('c.Translation ct')
            ->innerJoin('c.County co')
            ->innerJoin('co.Country cou')
            ->where('ct.name LIKE ?', $cityTerm)
            // ->andWhere('co.name LIKE ?', array($countyTerm))
            ->andWhere('cou.name LIKE ? OR cou.name_en LIKE ?', array($countryTerm, $countryTerm));
        
        if ($query->count() > 0) {
            $c = $query->fetchOne();
        } else {          
            $cult = sfContext::getInstance()->getUser()->getCulture();  
            sfContext::getInstance()->getUser()->setCulture('en');
            $county = CountyTable::findOrCreate($county, $country);
            $partnerClass = getlokalPartner::getLanguageClass();
            
            if ($county->getCountry()->getSlug()) {
                $partnerClass = strtolower($county->getCountry()->getSlug());
            }
            
            $c = new City();
            $c->setName($city);
            $c->setCounty($county);
            $c->setLat($lat);
            $c->setLng($lng);
            $c->setSlug(self::slugify($c->getName()));
            $c->save();
            sfContext::getInstance()->getUser()->setCulture($cult);
        }
        
        return $c;
    }

    public static function geocodeUrl($address = null, $lat = null, $lng = null, $addressPrior = false) {
        $url = null;
        if ($addressPrior) {
            if (!empty($address)) {
                $address = urlencode($address);
                $url = "address={$address}";
            } elseif (!empty($lat) && !empty($lng)) {
                $url = "latlng={$lat},{$lng}";
            }
        } else {
            if (!empty($lat) && !empty($lng)) {
                $url = "latlng={$lat},{$lng}";
            } elseif (!empty($address)) {
                $address = urlencode($address);
                $url = "address={$address}";
            }
        }

        return $url;
    }

    public static function slugify($str, $options = array()) {
        // Make sure string is in UTF-8 and strip invalid UTF-8 characters
        $str = mb_convert_encoding((string)$str, 'UTF-8', mb_list_encodings());

        $defaults = array(
            'delimiter' => '-',
            'limit' => null,
            'lowercase' => true,
            'replacements' => array(),
            'transliterate' => true,
        );

        // Merge options
        $options = array_merge($defaults, $options);

        $char_map = array(
            // Latin
