<?php

/**
 * ConversationTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ConversationTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ConversationTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Conversation');
    }
    
 public function getConversation($page_from, $page_to)
  {
  
    $return = $this->createQuery('c')
                ->where('c.page_from = ?', $page_from)
                ->andWhere('c.page_to = ?', $page_to)
                ->fetchOne();
    	
    if(!$return)
    {
      $return = new Conversation();
      $return->setPageFrom($page_from);
      $return->setPageTo($page_to);
      $return->save();
    }
    
    return $return;
  }
  
public static function getExistingConversation($page_from, $page_to)
  {
  
    $return = Doctrine::getTable('Conversation')
    ->createQuery('c')
                ->where('c.page_from = ?', $page_from)
                ->andWhere('c.page_to = ?', $page_to)
                ->fetchOne();
    	
   
    
    return $return;
  }
}