class Slider extends BaseSlider
{
  public function getTitle()
  {
    return sfContext::getInstance()->getUser()->getCulture() == 'en'? $this->getTitleEn(): $this->_get('title');
  }
  public function getBody()
  {
    return sfContext::getInstance()->getUser()->getCulture() == 'en'? $this->getBodyEn(): $this->_get('body');
  }
  public function getLink()
  {
    return sfContext::getInstance()->getUser()->getCulture() == 'en'? $this->getLinkEn(): $this->_get('link');
  }
  
  public function getThumb($size = 0)
  {
    return $this->getFile()->getThumb($size);
  }
}