class Voucher extends BaseVoucher
{
public function getIsValid()
  {
  	$today = date('Y-m-d');
  	$result= false;
  	$query = Doctrine_Core::getTable ( 'Voucher' )
  	->createQuery ( 'a' )
	->innerJoin('a.CompanyOffer co')
	->where('co.id = ?', $this->getId())		
	->andWhere('co.valid_from <= ?', $today)
    ->andWhere('co.valid_to >= ?', $today);
	
	$vouchers = $query->execute();
	if (count($vouchers) > 0)
	{
		$result = true;
	}
	return $result;
  }
  
  
}